ishfrmfield.script:


public void KeysField_ValueChanged(Object sender,	EventArgs e)
{
    //Check	here if	keysfield	contains invalid characters, see dita	1.2	lang.	spec.
    // { } [ ] ?
    if (this.Form.Fields.Contains("KeysField"))
    {
        Field keysField = this.Form.Fields["KeysField"];

        string pattern = @"(\?|,|\{|\}|\[|\|/|#])";
        string readablePattern = @"'?'	','	 '{',	'}'	 '[',	']', '/' , '#'";
        Regex re = new Regex(pattern);
        if (re.IsMatch(keysField.SerializedValue))
        {
            keysField.SerializedValue = re.Replace(keysField.SerializedValue,	"");
            throw new	Exception(string.Format("The following special characters	are	not	permitted	in the Key Defintion field:\r\n'{0}'.\r\nOnly	a	single key is	permitted.", readablePattern));
        }
    }
}
